      assert.deepEqual(messages[0].ruleId, 'vue/no-duplicate-attributes')
      assert.deepEqual(messages[0].line, 4)
      assert.deepEqual(messages[1].ruleId, 'vue/html-quotes')
      assert.deepEqual(messages[1].line, 4)
